import React, { useState }from 'react';
import './App.css';
import Card from './Card';
import Menu from './Menu';

export default function App() {
  const [dice, setDice] = useState(1);
  const [playerTurn, setPlayerTurn] = useState('red');
  const [player1TurnScores,setPlayer1TurnScore] = useState([]);
  const [player1TotalScore, setPlayer1TotalScore] = useState(0);

  function rollDice () {
    const newRoll = Math.floor(Math.random() * 6 + 1);

    setDice(newRoll);

    return newRoll;
  }

  function setTotalScore () {
    let totalScore = 0;

    if (player1TurnScores.length > 0) {
      totalScore = player1TotalScore.reduce((a, b) => a + b);
    } else {
      return 0;
    }
  }
  setPlayer1TotalScore(setTotalScore());


  return (
    <div>
      Dice game
      <Menu
        player1Dice={dice}
        getDice={rollDice}
        player={playerTurn}
        handlePlayer={setPlayerTurn}
        player1TurnScores={player1TurnScores}
        handlePlayer1TurnScore={setPlayer1TurnScore}
      />
      <Card
        newDice={dice}
        player={playerTurn}
        player1TurnScores={player1TurnScores}
        player1TotalScore={player1TotalScore}
      />
    </div>
  );
}
