export default function Menu({getDice, player, handlePlayerTurn}) {

  function turnPlayer (player) {
    player === 'red' ? handlePlayerTurn('yellow') : handlePlayerTurn('red')
  }


  function throwBtn() {
    getDice();
    turnPlayer();
  }


    return (
        <div className="menus">
            <button onClick={throwBtn}>던지기</button>
            <button>처음부터</button>
        </div>
    )
}
