export default function Menu({
  player1Dice,
  getDice,
  player,
  handlePlayer,
  player1TurnScores,
  handlePlayer1TurnScore,
  player1TotalScore,
  handlePlayer1TotalScore
}) {
  let player1Score = [...player1TurnScores];

  function turnPlayer () {
    if (player === 'red') {
      handlePlayer('yellow')
    } else if (player === 'yellow') {
      handlePlayer('red')
    }
  }

  function playerHistory () {
    player1Score.push(player1Dice);

    console.log(1,player1Score)
    handlePlayer1TurnScore(player1Score);
  }

  function setTotalScore () {
    let totalScore = 0;

    if (player1TurnScores.length > 0) {
      console.log(2,player1Score)
      totalScore = player1Score.reduce((a, b) => a + b);
      handlePlayer1TotalScore(totalScore);
    } else {
      handlePlayer1TotalScore(player1Dice);
    }
  }

  function throwBtn() {
    getDice();
    turnPlayer();
    playerHistory();
    setTotalScore();
  }

    return (
        <div className="menus">
            <button onClick={throwBtn}>던지기</button>
            <button>처음부터</button>
        </div>
    )
}
