export default function Menu({getDice, player, handlePlayer}) {

  console.log()
  function turnPlayer (player) {
    if (player === 'red') {
      handlePlayer('yellow')
    } else if (player === 'yellow') {
      handlePlayer('red')
    }
  }

  function throwBtn() {
    getDice();
    turnPlayer();
  }


    return (
        <div className="menus">
            <button onClick={throwBtn}>던지기</button>
            <button>처음부터</button>
        </div>
    )
}
