export default function Menu({
  player1Dice,
  getDice,
  handlePlayer1Dice,
  player,
  handlePlayer,
  player1TurnScores,
  handlePlayer1TurnScore,
  handlePlayer1TotalScore,
  handlePlayer2TurnScore,
  handlePlayer2TotalScore,
}) {
  let player1Score = [...player1TurnScores];
  let player2Score = [...player2TurnScores];


  function turnPlayer () {
    if (player === 'red') {
      handlePlayer('yellow')
    } else if (player === 'yellow') {
      handlePlayer('red')
    }
  }

  function playerHistory () {
    if(player === 'yellow')
    player1Score.push(player1Dice);

    console.log(1,player1Score)
    handlePlayer1TurnScore(player1Score);
  }

  function setTotalScore () {
    let totalScore = 0;

    if (player1TurnScores.length > 0) {
      console.log(2,player1Score)
      totalScore = player1Score.reduce((a, b) => a + b);
      handlePlayer1TotalScore(totalScore);
    } else {
      handlePlayer1TotalScore(player1Dice);
    }
  }

  function resetTurn () {
    handlePlayer1Dice(null);
    handlePlayer('yellow');
    handlePlayer1TurnScore([]);
    handlePlayer1TotalScore(0);
    getDice();

  }

/**함수묶음(주사위 던지기, 사용자 변경, 눈 기록, 총점수) */
  function throwBtn() {
    getDice();
    turnPlayer();
    playerHistory();
    setTotalScore();
  }

    return (
        <div className="menus">
            <button onClick={throwBtn}>던지기</button>
            <button onClick={resetTurn}>처음부터</button>
        </div>
    )
}
