export default function Menu({
  player1Dice,
  getDice,
  player,
  handlePlayer,
  player1TurnScores,
  handlePlayer1TurnScore,
  player1TotalScore,
  handlePlayer1TotalScore
}) {
  function turnPlayer () {
    if (player === 'red') {
      handlePlayer('yellow')
    } else if (player === 'yellow') {
      handlePlayer('red')
    }
  }

  function playerHistory () {
    let newHistory = null;

    if (player1TurnScores.length === 0) {
      newHistory = [...player1TurnScores, `${player1Dice}`];
    } else {
      newHistory = [...player1TurnScores, `, ${player1Dice}`];
    }

    handlePlayer1TurnScore(newHistory);
  }

  function setTotalScore () {
    let totalScore = 0;
    console.log(player1TotalScore)
    if (player1TurnScores.length > 1) {
      totalScore = player1TotalScore.reduce((a, b) => a + b);
      handlePlayer1TotalScore(totalScore);
    } else {
      handlePlayer1TotalScore(player1TotalScore);
    }
  }


  function throwBtn() {
    getDice();
    turnPlayer();
    playerHistory();
  }


    return (
        <div className="menus">
            <button onClick={throwBtn}>던지기</button>
            <button>처음부터</button>
        </div>
    )
}
