import React, { useState }from 'react';
import './App.css';
import Card from './Card';
import Menu from './Menu';

export default function App() {
  const [dice, setDice] = useState(1);
  const [playerTurn, setPlayerTurn] = useState('red');
  const [player1TurnScores,setPlayer1TurnScore] = useState([]);

  function rollDice () {
    const newRoll = Math.floor(Math.random() * 6 + 1);

    setDice(newRoll);

    return newRoll;
  }

  return (
    <div>
      Dice game
      <Menu getDice={rollDice} player={playerTurn} handlePlayer={setPlayerTurn}/>
      <Card newDice={dice} player={playerTurn}/>
    </div>
  );
}
