import React, { useState }from 'react';

export default function Card({
  name,
  newDice,
  player,
  player1TurnScores,
  player1TotalScore
}) {
  let diceImg = '';
  const player1History = player1TurnScores.join(', ');

  if(!newDice) {
    diceImg = `${player}01`
  } else {
    diceImg = `${player}0${newDice}`
  }

  console.log(diceImg)
  return(
    <div className="card">
      <h4>{name}</h4>
      <img className='dices' src={require(`./image/dice-${diceImg}.png`)} alt={diceImg}/>
      <p>총점</p>
      <p>{player1TotalScore}</p>
      <p>기록</p>
      <p>{player1History}</p>
    </div>
  )
}
