import React, { useState }from 'react';
import './App.css';
import Card from './Card';
import Menu from './Menu';

export default function App() {
  const [dice, setDice] = useState(0);
  const [playerTurn, setPlayerTurn] = useState(`red`);


  function rollDice () {
    const newRoll = Math.floor(Math.random() * 5) + 1;

    setDice(newRoll);

    return newRoll;
  }
  console.log(playerTurn, dice)
  function turnPlayer (playerTurn) {
    playerTurn === 'red' ? setPlayerTurn('yellow') : setPlayerTurn('red')
  }

  return (
    <div>
      Dice game
      <Menu getDice={rollDice} handlePlayer={setPlayerTurn}/>
      <Card newDice={dice} player={playerTurn}/>
    </div>
  );
}
