export default function Menu({
  playerDice,
  getDice,
  handlePlayer1Dice,
  player,
  handlePlayer,
  hadleplayerdice,
  handlePlayer1TurnScore,
  handlePlayer1TotalScore,
  handlePlayerHistory,
}) {
  function turnPlayer () {
    if (player === 'red') {
      handlePlayer('yellow')
    } else if (player === 'yellow') {
      handlePlayer('red')
    }
  }

  /**리셋 버튼 */
  function resetTurn () {
    handlePlayer1Dice(null);
    handlePlayer('yellow');
    handlePlayer1TurnScore([]);
    handlePlayer1TotalScore(0);
    getDice();

  }

/**함수묶음(주사위 던지기, 사용자 변경, 눈 기록, 총점수) */
  function throwBtn() {
    getDice();
    turnPlayer();
    handlePlayerHistory();
    hadleplayerdice();
  }

    return (
        <div className="menus">
            <button onClick={throwBtn}>던지기</button>
            <button onClick={resetTurn}>처음부터</button>
        </div>
    )
}
