import React, { useState, useEffect }from 'react';
import './App.css';
import Card from './Card';
import Menu from './Menu';

export default function App() {
  const [dice, setDice] = useState(null);
  const [playerTurn, setPlayerTurn] = useState('red');
  const [player1TurnScores,setPlayer1TurnScore] = useState([]);
  const [player1TotalScore, setPlayer1TotalScore] = useState(0);

  useEffect(() => {
    rollDice();
  },[]);

  /**주사위 난수 생성 */
  function rollDice () {
    const newRoll = Math.floor(Math.random() * 6) + 1;

    setDice(newRoll);
  }

  return (
    <div className='contents'>
      <img className='logo' src={require('./image/logo.png')} alt={'logo'} />
      <h3>
        Dice game
      </h3>
      <Menu
        player1Dice={dice}
        handlePlayer1Dice={setDice}
        getDice={rollDice}
        player={playerTurn}
        handlePlayer={setPlayerTurn}
        player1TurnScores={player1TurnScores}
        handlePlayer1TurnScore={setPlayer1TurnScore}
        handlePlayer1TotalScore={setPlayer1TotalScore}
      />
      <div className='player-info'>
        <Card
          name={player1}
          newDice={dice}
          player={playerTurn}
          player1TurnScores={player1TurnScores}
          player1TotalScore={player1TotalScore}
        />
        <Card
          newDice={dice}
          player={playerTurn}
          player1TurnScores={player1TurnScores}
          player1TotalScore={player1TotalScore}
        />
      </div>
    </div>
  );
}
